class InstallerApp:
    def __init__(self, root):
        self.root = root
        self.root.title("Installer")
        self.root.geometry("460x260")  # Set window size
        self.root.resizable(width=False, height=False)  # Disable window resizing
        self.queue = Queue()
        self.image_label = None
        self.errors_detected = False
        self.error_label = tk.Label(self.root, text="Placeholder", padx=10, pady=10)
         # Load the base64 image and display it
        base64_image = b""
        self.image = Image.open(io.BytesIO(base64.b64decode(base64_image)))
        self.photo = ImageTk.PhotoImage(self.image)
        self.image_label = tk.Label(self.root, image=self.photo)
        self.image_label.pack()

        self.create_widgets()
        self.check_installation_directory()

    def clear_widgets(self):
        for widget in self.root.winfo_children():
            if widget != self.image_label:  # Skip clearing the image label
                widget.destroy()

    def check_installation_directory(self):
        script_directory = os.path.dirname(os.path.abspath(__file__))
        required_files = ['modsList.txt', 'icon.ico', 'do NOT readme.txt']

        missing_files = [file for file in required_files if not os.path.isfile(os.path.join(script_directory, file))]

        if missing_files:
            self.clear_widgets()
            error_message = (
                "Error: You are not in the correct installation directory. "
                "Please make sure you are in your Psych Engine root, where the .exe is."
            )
            error_label = tk.Label(self.root, text=error_message, padx=10, pady=10, wraplength=400, justify='center')
            error_label.pack()

            self.close_button = tk.Button(self.root, text="Close", command=self.root.quit, state=tk.NORMAL)
            # Pack the close button only if it's not already packed
            if self.close_button.winfo_ismapped() == 0:
                self.close_button.pack(pady=10)

    def create_widgets(self):
        welcome_message = (
            "Welcome to the Psych Engine with Extra Keys installer! "
            "Please connect to the internet before installing."
        )
        self.label = tk.Label(self.root, text=welcome_message, padx=10, pady=5, wraplength=400, justify='center')
        self.label.pack()

        self.download_button = tk.Button(self.root, text="Download and Install", command=self.start_download)
        self.download_button.pack(pady=10)

    def create_download_widgets(self):
        self.progress_label = tk.Label(self.root, text="", wraplength=300, justify='center')
        self.progress_label.pack()

        self.progress_bar = ttk.Progressbar(self.root, length=400, mode="determinate")
        self.progress_bar.pack()

        # Add a frame to hold the buttons
        self.button_frame = tk.Frame(self.root)
        self.button_frame.pack(pady=10)

        # Add the "Gamebanana" button
        self.gamebanana_button = tk.Button(self.button_frame, text="GameBanana", command=self.open_gamebanana)
        self.gamebanana_button.pack(side=tk.LEFT, padx=5)

        # Add the Close button
        self.close_button = tk.Button(self.button_frame, text="Close", command=self.root.quit, state=tk.DISABLED)
        self.close_button.pack(side=tk.LEFT, padx=5)

    def start_download(self):
        self.create_download_widgets()
        self.download_button.config(state=tk.DISABLED)
        self.download_button.destroy()
        threading.Thread(target=self.download_files).start()

    def download_files(self):
        for entry in downloads:
            self.queue.put(entry)

        while not self.queue.empty():
            entry = self.queue.get()
            self.download_file(entry)

        self.finish_installation()

    def download_file(self, entry):
        url = entry['url']
        filename = entry['filename']
        script_directory = os.path.dirname(os.path.abspath(__file__))
        directory = script_directory
        file_directory = os.path.join(directory, entry['directory'])
        filepath = os.path.join(file_directory, filename)

        print(f"Downloading {filename} from {url} to {os.path.abspath(filepath)}")

        try:
            response = requests.get(url, stream=True)
            response.raise_for_status()  # Raise an exception for bad status codes

            total_size = int(response.headers.get('content-length', 0))
            downloaded_size = 0
            chunk_size = 1024  # 1 KB

            os.makedirs(directory, exist_ok=True)

            with open(filepath, 'wb') as file:
                for chunk in response.iter_content(chunk_size=chunk_size):
                    if chunk:
                        file.write(chunk)
                        downloaded_size += len(chunk)
                        progress = (downloaded_size / total_size) * 100
                        self.update_progress(filename, downloaded_size, total_size, progress)
                self.queue.task_done()
        except requests.exceptions.RequestException as e:
            error_messaages = f"Error downloading {filename}: {str(e)}"
            self.progress_label.config(text=error_messaages)
            self.errors_detected = True
            return

    def update_progress(self, filename, downloaded_size, total_size, progress):
        percentage = f"{progress:.2f}%"

        self.progress_label.config(text=f"Downloading and writing file: {filename} - {progress:.2f}% completed - {bytes_to_megabytes(downloaded_size)} MB / {bytes_to_megabytes(total_size)} MB")
        self.progress_bar['maximum'] = total_size
        self.progress_bar['value'] = downloaded_size
        self.root.title(f"{filename} - {percentage}")
        self.root.update()

    def finish_installation(self):
        if self.errors_detected:
            print("Errors were detected while the installation was in progress")
        else:
            self.progress_label.config(text=f"Completed: Files downloaded successfully")
            
        self.close_button.config(state=tk.NORMAL)

    def close_installer(self):
        self.root.quit()

    def open_gamebanana(self):
        webbrowser.open_new("https://www.gamebanana.com/mods/333373")

if __name__ == "__main__":
    root = tk.Tk()
    app = InstallerApp(root)
    root.mainloop()